// --- noise ---------------------------------------------------------
//#define T(u) texture(iChannel0,.01*u/iChannelResolution[0].xy).x // interp text noise
  #define T(u) ( .5 + 1.5* noise (.005*u) )  // base gradient noise
//#define T(u) ( .5 + 1.5* perlin(.005*u) )  // perlin noise

#define hash(p )     ( 2.* fract(sin((p)*mat2(127.1,311.7, 269.5,183.3)) *43758.5453123) - 1. ) \
                    *  mat2(cos(iTime+vec4(0,11,33,0)))
#define grad(x,y)      dot( hash( i+vec2(x,y) ), f-vec2(x,y) )

float noise( vec2 p )
{
    vec2 i = floor(p), f = fract(p), // u = f*f*(3.-2.*f);              // = smoothstep
                                        u = f*f*f*( 10. +f*(6.*f-15.)); // better with derivatives
    return mix( mix( grad(0,0), grad(1,0), u.x),
                mix( grad(0,1), grad(1,1), u.x), u.y);
}

float perlin( vec2 U ) { // inspired from https://www.shadertoy.com/view/XdXGW8
    float v = 0., s = .5;
    mat2 m = mat2( 1.6,  1.2, -1.2,  1.6 );
    for( int i=0; i<3; i++, s/=2., U*=m )
        v  += s * noise( U ); 
    return v;
}
// ---------------------------------------------------------------

#define cross(a,b) ( (a).x*(b).y - (a).y*(b).x )

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = u/32., I = floor(U), V, A;
    float psi = T(u);
    V = 5e3 * vec2( -dFdy(psi), dFdx(psi) ); // div(V) = 0
    U = fract(U)-.5;
    A = vec2( dot(U,V) , cross(U,V) );       // draw local frame ( orthoV, V )
    A = sin(A); // A = 1./abs(A); 
    O = .5 + .5 * vec4( A , 0,0);            // colored frame
    O = vec4( A.x + A.y );                   // dots
    O = vec4(  max( A.x, A.y ) );            // frame
    O *= .4 + .6* length(V)/40.;             // intensity = speed 
 // O.r = min(O.r, length(V)/40. );
 // U = .5+.5*cos(3.14*U); O *= U.x*U.y;     // tile vignetting

