const float PI = 3.1416;
mat2 rotationMatrix(float angle){
	angle *= PI / 180.0;
    float s=sin(angle), c=cos(angle);
    return mat2( c, -s, s, c );
}

float bckgr(vec2 uv){
    return step(.0,(cos(uv.x/.0255)+0.9988)/(cos(uv.y/.02555)+0.9988));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
   float ar = iResolution.x/iResolution.y;
   float d = smoothstep(0.,.09*ar,length(uv/.5));
   d *= bckgr(uv);
   uv *= rotationMatrix(iTime/PI);
   float r = .025*sin(cos(atan(uv.x, uv.y) * 25.));
   float f = smoothstep(0.,.06*ar, length(r));
   fragColor = vec4(vec3(f-(1.-d))*10., 1.0);
}
