float f_n_dist_to_line(
    vec2 o_p1, 
    vec2 o_p2,
    vec2 o_pixel_coord_norm
){

    //y = m*x + b
    // y = m*(x-x1) + y1;
    if(o_p1.x > o_p2.x){
        vec2 o_p_tmp = o_p1; 
        o_p1 = o_p2;
        o_p2 = o_p_tmp;
    }
    vec2 o_delta = o_p2-o_p1; 

    float n_m = o_delta.y/o_delta.x;

    float n_y = n_m * (o_pixel_coord_norm.x - o_p1.x) + o_p1.y;

    float n_dist_line = length(o_pixel_coord_norm-vec2(o_pixel_coord_norm.x, n_y));

    if(
        o_pixel_coord_norm.x < min(o_p1.x, o_p2.x)
    ){
        n_dist_line = length(o_pixel_coord_norm-o_p1);
        n_dist_line = 1.0;
    }
    if(
        o_pixel_coord_norm.x > max(o_p1.x, o_p2.x)
    ){
        n_dist_line = length(o_pixel_coord_norm-o_p2);
        n_dist_line = 1.0;
    }

    return n_dist_line;


}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o_pixel_coord_norm = fragCoord.xy / iResolution.xy;
    vec2 o_mouse_coord_norm = iMouse.xy / iResolution.xy;
    if(iMouse.w==0.0){o_mouse_coord_norm =vec2(0.5,0.5);}
    o_pixel_coord_norm -= 0.5;
    float n_tau = 6.283185;
    o_pixel_coord_norm.x *= iResolution.x/iResolution.y;

    o_pixel_coord_norm *=10.0*o_mouse_coord_norm.x;
    float n_amp = 0.5; 
    float n_lines = 99.0;

    float delta_x = o_pixel_coord_norm.x - 0.0;
    float delta_y = o_pixel_coord_norm.y - 0.0;
    float theta_radians = atan(delta_y, delta_x);
    float n_nth_line = theta_radians/(n_tau / n_lines);
    float n_radians = n_tau * n_nth_line; 

    float n_x = sin(n_radians)*n_amp;
    float n_y = cos(n_radians)*n_amp;

    float n_dist_to_line = f_n_dist_to_line(
        vec2(0.0, 0.0),
        vec2(n_x, n_y), 
        o_pixel_coord_norm
    );

    fragColor = vec4(n_dist_to_line);
}
