void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 normCoord = fragCoord/iResolution.xy * 2.0 - 1.0;
    vec2 normMouse = iMouse.xy/iResolution.xy;

    float angle = iTime * 0.1;

    for (float i=0.0; i<32.0; i+=1.0)
    {
        normCoord = abs(normCoord);
        normCoord -= 0.5;
        normCoord *= 1.1;
        normCoord *= mat2(
            cos(angle), -sin(angle),
            sin(angle), cos(angle)
        );
    }

    vec3 col = vec3(
        length(normCoord)*normMouse.x,
        length(normCoord)*normMouse.y,
        length(normCoord)
    );
    //col *= iMouse.xy

    // Output to screen
    fragColor = vec4(col, 1.0);
}
