vec3 palette (float t) {
    vec3 a = vec3(1.00, 0.50, 0.66);
    vec3 b = vec3(0.50, 0.26, 0.50);
    vec3 c = vec3(0.35, 0.24, 0.50);
    vec3 d = vec3(0.16, 0.30, 0.93);
    return a + b*cos(6.28318*(c*t+d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for (float i = 0.0; i < 4.0; i++) {

        if (i >= 1.0) uv.x = -uv.x;

        uv = uv - .5 + i;

        uv *= sin(i - length(uv));

        float d = length(uv) * exp(-length(uv0));

        vec3 col = palette(length(uv) - iTime*0.3);

        d = sin(d*5.+iTime*0.33);
        if (i >= 1.0) {
          if (d < 0.0) {
            d = smoothstep(0.0, 1., abs(d));
          }
        }

        if (i == 3.) d += pow(0.1/d, 1.);

        finalColor += col * d;
    }   

    finalColor *= 0.2/length(uv0);

    fragColor = vec4(finalColor, 1.0);
}
