void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec3 col = vec3(225.0,0.0,0.0);
    vec2 r = vec2(fragCoord.x/iResolution.x, fragCoord.y/iResolution.y);



    if(fragCoord.x < iResolution.x /3.0){
      col = vec3(0.0,0.0,0.0);

    }
    else if (iResolution.x /3.0 < fragCoord.x){
      if(fragCoord.x < 2.0*iResolution.x /3.0){
         col = vec3(225.0,225.0,0.0);

      }

    }


    // Output to screen
    fragColor = vec4(col,1.0);
}
