void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    //fragColor = vec4(col,1.0);
    fragColor = texture(iChannel0, uv)*0.2 + texture(iChannel1,uv) * 0.8;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 delta = vec2(1) / iResolution.xy;

    fragColor = texture(iChannel0, uv + delta*5.*sin(iTime*25.)).grab*sin(iTime)*2.5
              + texture(iChannel0, uv + delta*vec2(-20.,23.)*sin(iTime*32.)).brag*sin(iTime*1.7)*2.5
              - texture(iChannel0, uv + delta*vec2(-1.5,0)).garb*cos(iTime*2.3)*2.5
              + texture(iChannel0, uv + delta*vec2(-1.5,-1.0)*tan(iTime*0.05)).barg*cos(iTime*0.7)*2.5;

    if (uv.y > 0.95) fragColor = vec4(0);

    //if (fragColor.b > 0.9) fragColor = fragColor.argb * vec4(.5,.5,1.,0.5);
    //if (fragColor.r < 0.1) fragColor = fragColor.bgra * vec4(1.,1.5,1.,1.);
    fragColor *= vec4(0.22,0.25,0.22,0.22);

    //vec2 p = iMouse.xy/iResolution.xy;
    vec2 p = vec2(cos(iTime*7.), cos(iTime*11.)) * 0.25 + 0.5;
    if (distance(uv, p) < 0.015) 
    {
        if (fragColor.r < 0.5)
        {
            fragColor += vec4(1.5,0.,0.5,0.);
        }
    }

    p = vec2(cos(iTime*5.), sin(iTime*13.)) * 0.25 + 0.5;
    if (distance(uv, p) < 0.025) 
    {
        if (fragColor.g < 0.5)
        {
            fragColor += vec4(0.,1.5,0.,0.5);
        }
    }


    fragColor = clamp(fragColor, vec4(-1.2), vec4(1.1));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    fragColor = texture(iChannel1, (uv-.7)*0.9915 + .7) * 0.6 + texture(iChannel0, (uv-.6)*1.01 + .6) * 0.6;
}
