#define PI 3.14159265358979323846

float circle(in vec2 uv, in vec2 center, in float radius){

    return 1.-smoothstep(0., 0.01, length(uv-center)-radius);
}
float length_(vec2 uv, vec2 st, float N){

    return pow(pow(abs(uv.x-st.x), N) + pow(abs(uv.y-st.y), N), 1./N);
}
mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix( vec3(1.0), rgb, c.y);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( 2.*fragCoord.xy - iResolution.xy ) / iResolution.y;
    uv.x *=  iResolution.y/iResolution.x;
    vec3 col = vec3(0.);
    //uv.y -=(-3.+mod(iTime,6.));
    //uv = pow(abs(uv),vec2(1.25+.75*cos(iTime/4.)));;
    uv = pow(abs(uv),vec2(1./3.)+(1.+cos(iTime)));
    // Time varying pixel color
    //uv = abs(uv);
    //vec3 col = vec3(0.);
    int nb_bulles = 15;
    for(int i =1; i<=nb_bulles;i++){
        //uv.y+=mod(float(i),3.);
        float vit = 0.1*float(i);
        uv*=rotate2d((-2.*mod(float(i),2.)+1.)*iTime*0.5+float(i));
        vec2 center1 = vec2(mod(0.8*float(i),3.)*cos(iTime*vit),mod(0.2*float(i),1.)*sin(iTime*vit));
        vec2 center2 = vec2(-mod(0.4*float(i),3.)*cos(iTime*vit),mod(0.6*float(i),1.)*sin(iTime*vit));
        float d1 = length_(uv,center1, 2.);
        float d2 =  length_(uv,center2, 2.);
        vec2 st = vec2(d1,d2)/(d1+d2);
        float rayon = sin(iTime-float(i))*0.5+0.5;
        col+=circle(uv, st,rayon)*hsb2rgb(vec3(float(i)/float(nb_bulles),0.9,0.9*pow(length_(st,uv,2.)/rayon,5.)));
    }
    //vec3 col = 0.5 + 0.5*cos(iTime+st.xyx+vec3(0,2,4));


    // Output to screen
    fragColor = vec4(col,1.0);
}
