void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pt1=iResolution.xy/2.;
    vec2 pt2=iResolution.xy/2.1;
    vec2 pt3=iResolution.xy/2.4;
    vec3 clr=vec3(1.,0.,0.);
    float lim1=0.5;
    float lim2=5.;
    float lim3=10.;
    if(pt1.x-fragCoord.x<lim1&&fragCoord.x-pt1.x<=lim1&&pt1.y-fragCoord.y<lim1&&fragCoord.y-pt1.y<=lim1) fragColor=vec4(clr,1.);
    if(pt2.x-fragCoord.x<lim2&&fragCoord.x-pt2.x<=lim2&&pt2.y-fragCoord.y<lim2&&fragCoord.y-pt2.y<=lim2) fragColor=vec4(clr,1.);
    if(pt3.x-fragCoord.x<lim3&&fragCoord.x-pt3.x<=lim3&&pt3.y-fragCoord.y<lim3&&fragCoord.y-pt3.y<=lim3) fragColor=vec4(clr,1.);
}
