float rand(float co) { return fract(sin(co*(91.3458)) * 47453.5453); }
float rand(vec2 co){ return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453); }
float rand(vec3 co){ return rand(co.xy+rand(co.z)); }

vec3 palette(float t) {
    vec3 a = vec3(0.500, 0.500, 0.500);
    vec3 b = vec3(0.500, 0.500, 0.500);
    vec3 c = vec3(1.000, 1.000, 1.000);
    vec3 d = vec3(0.000, 0.333, 0.667);
    return a + b * cos(6.28318 * (c * t + d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)    
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;

    uv0 += vec2(sin(iTime * 0.6) * 0.5, cos(iTime * 0.5) * 0.5);
    uv += vec2(cos(iTime * 0.3) * 0.1, sin(iTime * 0.2) * 0.1);
    uv *= (sin(iTime * 0.1) + 1.0) / 20.0 + 0.1;

    float sum = 0.0;
    int count = 0;
    for(float i = 0.001; i < 0.02; i += 1.0/1024.0) {
        sum += texture(iChannel0, vec2(i, 0.25)).x;
        count++;
    }
    float average = sum/float(count);
    float hit = max(average - 0.8, 0.0);

    float d = length(uv);
    d -= average * 0.04;

    vec3 col = palette(length(uv0) * 0.2 - iTime * 0.1);
    col += rand(uv0) * 4.0 * hit;

    d = sin(d * 8.0 + iTime + cos(iTime * d)) / 8.0;
    d = abs(d);

    d = (0.02 + hit * 0.02) / d;

    col *= d;

    // Output to screen
    fragColor = vec4(col, 1.0);
}
