void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    uv -= 0.5;
    float ratio = iResolution.x / iResolution.y;
    uv.x *= ratio;
    float zoom = 1.0;
    uv *= zoom;

    // (x - a)^2 + (y - b)^2 = r^2
    float x, y, r, lhs, rhs;
    x = uv.x;
    y = uv.y;
    r = 0.25;
    vec2 M = vec2(cos(iTime) * 0.5, sin(iTime) * r);
    lhs = pow(x - M.x, 2.0) + pow(y - M.y, 2.0);
    rhs = r * r;
    vec3 col = vec3(0.05f, 0.1f, 0.15f);
    if (lhs <= rhs)
    {
        col = vec3(0.99, 0.07, 0.33);
    }
    fragColor = vec4(col, 1);
}
