// ChatGPT generated shader.

// Fragment shader
precision highp float;


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 st = fragCoord.xy / iResolution.xy;
  vec2 pos = vec2(0.5, 0.5) + 0.1 * vec2(cos(iTime), sin(iTime));
  float d = distance(st, pos);

  vec3 color = vec3(0.0);

  // Ajout de motifs abstraits à la couleur
  color += vec3(0.3, 0.2, 0.1) * smoothstep(0.0, 0.1, sin(d * 100.0));
  color += vec3(0.2, 0.1, 0.3) * smoothstep(0.0, 0.05, sin(d * 150.0));
  color += vec3(0.1, 0.3, 0.2) * smoothstep(0.0, 0.03, sin(d * 200.0));
  color += vec3(0.3, 0.3, 0.0) * smoothstep(0.0, 0.02, sin(d * 250.0));

  // Assombrissement de la couleur pour évoquer la nuit
  color *= 0.3;

  fragColor = vec4(color, 1.0);
}
