float drawLine( float a, float b, float w ) {
    return step(a, b) + step(b, a-w);
}
float drawRect( float top, float height, float right, float width, vec2 st ) {
    float offset = .0001; // need this to hide position crack for some reason, figure this out later
    return drawLine(top+offset, st.y, height+offset) + drawLine(right+offset, st.x, width+offset*2.);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord){
    vec2 st = (fragCoord/iResolution.yy);// - vec2(16./9.,1.)/4.;
    vec3 color = vec3(0.0);

    // black outlines
    float ylines = drawLine(.2, st.x, .02) * drawLine(.7, st.x, .02) * drawLine(.9, st.x, .02);
    float xlines = drawLine(.6, st.y, .02) * drawLine(.8, st.y, .02);
    float shortline = drawRect(.22, .02, 2.0, 1.8, st);

    // color rectangles
    float blue = drawRect(.2, .2, 2., 1.1, st) * drawRect(.2, .2, .88, .18, st) ;
    float red = drawRect(.78, .18, .18, .18, st) * drawRect(.58, .36, .68, .48, st) ;
    float yellow = drawRect(1., .2, .88, .18, st) * drawRect(.78, .18, .88, .18, st);


    float pct = ylines * xlines * shortline;
    color = vec3(pct);

    color = mix(vec3(0.,0.,1.), color, blue);
    color = mix(vec3(1.,0.,0.), color, red);
    color = mix(vec3(1.000,0.882,0.066), color, yellow);

    fragColor = vec4(color,1.0);
}
