vec3 sussy(vec3 x, vec3 y){

    vec3 a = max(abs(abs(x) - 4.), abs(y + sign(x)*.5));
    vec3 b = max(abs(abs(x*2.) - 5.) - 2., abs(abs(y*2.) - 2.));
    vec3 c = max(abs(abs(abs(x*2.) - 5.) - 2.), abs(y*2. + sign(x)*4.));

    return min(min(a,b),c) - 1.;
}

vec3 impostor(vec3 x, vec3 y){

    vec3 a = mod(x + 2.*round(y*.2 - round((x*5. + y*2.)/66.)*.6) - 6., 12.) - 6.;
    vec3 b = mod(y - 3.*round((x*5. + y*2.)/66.) - 2.5, 5.) - 2.5;

    return -sussy(a, b);
}

vec3 among(vec2 z){
    vec3 i = vec3(0,1,2);
    return sign(impostor(z.x - i*4.,z.y - i)) + 1.1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 us = (2.*fragCoord - iResolution.xy)/iResolution.y*15. + 2.*iTime;

    fragColor = vec4(among(us) ,1.0);
}
