#define PI acos(-1.)

mat2 rotate2D(float rad){
    return mat2(cos(rad), sin(rad), -sin(rad), cos(rad));
}

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e; vec3 p;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y,e)*1.5;
    p.y=log(length(p.yz));
    //p=vec3(log(length(p.yz)),tan(cos(p.x-iTime)),atan(p.z,p.x));
    p.xy*=rotate2D(iTime/PI);

    for(j=0.;j++<4.;)
        p=abs(abs(p)-.05)-.03,
        p-=vec3(p.x*p.x+p.y*p.y, 2.*p.x*p.y,p.z),
        d=max(min(p.y,p.z),p.y),
        d-=dot(p,p)/2e4;

    e+=d;
}o+=vec4(exp(-p*i),1)*3./i;
}
