// Green screen function from @iq 

// :D :D :D
vec4 compositeGreenScreenTexture(vec2 uv, vec4 background)
{

     vec4 tex = texture(iChannel0, uv);

     float maxrb = max( tex.r, tex.b );
     float k = clamp( (tex.g-maxrb)*5.0, 0.0, 1.0 );

     float ll = length( tex );
     tex.g = min( tex.g, maxrb*0.8 );
     tex = ll*normalize(tex);

     return mix(tex, background, k);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float PI = 3.1415;
    mat2 rot = mat2(cos(PI/4.), -sin(PI/4.), sin(PI/4.), cos(PI/4.));
    vec2 uvrot = rot * uv;

    // Time varying pixel color
    float r = 0.;
    r = cos(uvrot.x*40.);
    float g = cos((uvrot.x + 0.2 + iTime/12.)*40.);
    float b = cos((uvrot.x + 0.4 - iTime/12.)*40.);
    vec4 col = vec4(r, g, b, 1.0);

    // Output to screen
    fragColor = col;
}
