#define r(a)   mat2(cos(a+vec4(0,11,33,0)))          //

void mainImage( out vec4 C, vec2 U )
{
    float o,i=0.,s,g=1.,k=.04;
    vec3 p,r,n,c;
    for(; i++ < 120.; g += o*k )
      { s = 1.;
        r = iResolution, 
        n=r-r,
        p = vec3( g* ( U - r.xy/2. ) / r.y, g-2. );        
        p.yz*=r(3.5);
        p.xz *= r(.1*iTime);
        k *= 1.035;
        c = p;
        for( o = length(sin(.5*p))-.1 ; s<10.; s/=.6 )
            p.xz *= r(1.),
            n += .15*cos(p),
            p = p*s+n.zxy,
            o = min(o, length(n));
        }        
    c = clamp(c,-6.,0.);
    c = .3 *(log(7.+ c));    
    C = vec4(c,1);	
}
