void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    for(float i = 0.0; i<=50.; i+=0.02)
    {
vec2 ew = vec2(fragCoord.xy*vec2(sin((iTime/100.)/15.)*sin(6.), sin(5.)));
vec3 ew2 = vec3(ew.x*fragCoord.y + sin(ew.y*fragCoord.x)+4.);
    vec2 uv = vec2 (fragCoord.yx/(iResolution.xy+fragCoord.yy*5.4525425));
    vec3 col = 0.8465433 + .3*cos(iTime+uv.xyx+ew2);
    vec3 tr = vec3(fragCoord.x*fragCoord.y/4., uv.x*
    (7.52*uv.y), col*vec3(5000.*cos(5.43),asin(i/24.764), sin(24.5342)/4.54
    *(fragCoord/vec2(54., 9.))));
    fragColor = vec4(col+tr,524.0);
    }
}
