void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

    vec4 col = vec4(0.);


    float scale = 60.;

    col = vec4(zoomNoise(fragCoord + vec2(30. * iTime), scale));

    int n = 100;

    for(int i = 0; i < n; i++){
        float div = float(n) / 30.;
        vec2 fragExt = vec2(cos(float(i) / div), sin(float(i) / div)) * (float(i) * scale / div);

        col += vec4(zoomNoise(fragCoord + fragExt + vec2(30. * iTime), scale));

    }

    col /= float(n + 1);


    fragColor = col;
}

float hash3(vec2 xy){
    xy = mod(xy, .19);
    float h = dot(xy.yyx,vec3(.013, 27.15, 2027.3));
    h *= h;
    h *= fract(h);

    return fract(h);
}
