void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2. - iResolution.xy) / iResolution.y;

    uv.x += iTime/3.333;
    float sine = sin(3. * uv.x) / 4.;
    float sinewave = smoothstep(0., .25, abs(uv.y - sine));

    //float coloredwave = abs(1.-sinewave);
    //coloredwave = 0.13 / 0.62 / coloredwave;

    float coloredwave = .02656 / sinewave; 

    vec3 colors = vec3(cos(iTime*5. + uv.x), cos(iTime*2. + uv.x), sin(iTime*3. + uv.x));

    vec3 wave = vec3(coloredwave, coloredwave, coloredwave);

    vec3 color = wave*colors;

    fragColor = vec4(1.-exp(-color), 1.0);
}
