void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;

    uv *= 2.;
    uv -= vec2(2.5, 0.5);
    uv = clamp(uv, 0., 1.);

	float frame = floor( mod( iTime*10.0, 6.0 ) );

    //vec2 offset = vec2(0.2);
    //float scale = 3.;


	float nyanWidthUV = 40.0f/256.0f;
	uv.x = (uv.x + frame)*nyanWidthUV;


	vec4 nyan = texture( iChannel0, uv );
	vec3 col = nyan.xyz;

    vec2 ruv = fragCoord.xy / iResolution.y;
    ruv -= vec2(0., 0.51f + 0.03f * sin(ruv.x * 10.0f + iTime * 5.0f));
    ruv.y = (ruv.y / 0.11f);

    vec3 rainbow_colors[7];
    rainbow_colors[0] = vec3(1.0f, 0.0f, 0.0f);
    rainbow_colors[1] = vec3(1.0f, 0.5f, 0.0f);
    rainbow_colors[2] = vec3(1.0f, 1.0f, 0.0f);
    rainbow_colors[3] = vec3(0.0f, 1.0f, 0.0f);
    rainbow_colors[4] = vec3(0.0f, 0.5f, 1.0f);
    rainbow_colors[5] = vec3(0.0f, 0.0f, 1.0f);
    rainbow_colors[6] = vec3(1.0f, 0.0f, 1.0f);

    if(ruv.x < 1.4f && abs(ruv.y) < 1.0f && length(col) > 1.73f)
    {
        float ry = (ruv.y * 0.5f) + 0.5f;
        for(int i = 1; i <= 7; i++)
        {
            if(ry < float(i) * (1.0f / 7.0f)){
                col = rainbow_colors[i - 1];
                break;
            }
        }
    }


    fragColor = vec4( col, 1.0 );
}
