void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 center = vec2(1.17, .5);
    vec3 col = vec3(0.);

    vec2 uv = fragCoord/iResolution.xy;
    uv.x*=iResolution.x/iResolution.y;
    uv-=center;

    float dist = length(uv);
    float radius = round(dist * 30.) / 30.;
    float speed = 4. * (1.0 - radius);
    float angle = acos(dot(normalize(vec2(sin(iTime * speed), cos(iTime * speed))), normalize(uv)));
    if (angle > 0.7) {
        col += smoothstep(.002, 0., abs(dist - radius));
    }

    fragColor = vec4(col, 1.);
}
