Main {
Q = vec4(0);
    for (int x = -1; x <= 1; x++)
    for (int y = -1; y <= 1; y++)
    {
        vec2 u = vec2(x,y);
        vec4 a = A(U+u);
        vec2 w1 = clamp(U+u+a.xy-0.5*r,U - 0.5,U + 0.5),
             w2 = clamp(U+u+a.xy+0.5*r,U - 0.5,U + 0.5);
        float m = (w2.x-w1.x)*(w2.y-w1.y)/(r*r);
        Q.xyz += m*a.w*a.xyz;
        Q.w += m*a.w;
    }
    if (Q.w>0.)
        Q.xyz/=Q.w;
        Q.z = mix(Q.z,sign(Q.z),.01);
