void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float a = 0.5625;
    uv.x /= a;
    vec2 p = vec2(0.5/0.5625, 0.5);
    vec2 q = uv - p;
    // Time varying pixel color
    vec3 col = vec3(0.3, 1.0, 0.1);
    float r = 0.2 + 0.1*cos(atan(q.x, q.y)*10.0 - 15.*q.x + 3.0);
    col *= smoothstep(r, r+0.01, length(q));

    //draw stem
    r = 0.015;
    col *=1.0 - (1.0 - smoothstep(r, r+0.01, abs(q.x))) * (1.0-smoothstep(0.0, 0.01, q.y));

    // Output to screen
    fragColor = vec4(col,1.0);
}
