vec3 palette (float t) {
    vec3 a = vec3(0.660, 0.560, 0.680);
    vec3 b = vec3(0.718, 0.438, 0.720);
    vec3 c = vec3(0.520, 0.100, 0.520);
    vec3 d = vec3(-0.60, -0.30, -0.09);
    return a + b*cos(6.28318*(c*t+d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for (float i = 0.0; i < 3.0; i++) {
        uv = uv - .5 + i;

        uv *= sin(i*5. - length(uv0));

        float d = length(uv) * exp(-length(uv0));

        vec3 col = palette(length(uv0) - iTime*.25);

        d = sin(d*9.+ iTime*.25) * 0.5;
        d = abs(d);

        d = pow(0.03/ d, 1.3);

        finalColor += col * d;
    }   

    finalColor *= 1.25/length(uv0);

    fragColor = vec4(finalColor, 1.0);
}
