void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 cFront=vec3(1.,0.,0.);
    vec3 cBack=vec3(0.,0.,1.);
    vec2 center=iResolution.xy/2.;
    float rds=iResolution.y*0.45;
    float dis=distance(center,fragCoord);
    float halfWidth=10.;
    float iSmth=smoothstep(rds-halfWidth, rds-halfWidth-1.5, dis);
    float oSmth=smoothstep(rds+halfWidth, rds+halfWidth+1.5, dis);
    vec3 iClr=mix(cFront,cBack,iSmth);
    vec3 oClr=mix(cFront,cBack,oSmth);

    if(iSmth>0.) fragColor=vec4(iClr,1.); else fragColor=vec4(oClr,1.);


}
