vec2 random2D(vec2 uv){
    vec2 random_vec2 = vec2(fract(sin(uv.x*123.+uv.y*456.)),fract(cos(uv.y*789.+uv.x*1234.)));
    return random_vec2;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

     uv*=10.;

    vec3 col = vec3(0);

    vec2 grad = fract(uv) - 0.5;//值域在 - 0.5~0.5
    vec2 id = floor(uv);//ID
    float mindist = 100.;




    for(int i = -1;i<2;i++){
        for(int j = -1;j<2;j++){


            vec2 offset = vec2(i,j);

            vec2 offset_point = random2D(id+offset);//对应ID随机点的坐标

            vec2 point_location = offset+sin(offset_point*iTime)*0.5;//和时间结合动起来

            float f = length(grad-point_location);//计算uv到顶点的距离

            if(mindist>f){
                mindist = f;//取最小值
            }



        }
    }

    col = vec3(mindist);

    vec3 color_add = vec3(0.1,0.2,0.4);

    vec3 col_f = vec3(0.0,(col.g+0.1)*(col.g+0.1)*0.9,(col.b+0.2)*(col.b*0.2)*0.9)+color_add;


    fragColor = vec4(col_f,1.0);
}
