vec3 palette(float t) {
    vec3 a = vec3(0.9176, 0.1686, 0.3294);
    vec3 b = vec3(0.7647, 0.1176, 0.1059);
    vec3 c = vec3(1.338, .84, 1.565);
    vec3 d = vec3(0.736, 1.992, 3.058);

    return a + b * cos(6.28318 * (c * t + d));
}

float sdHeart(in vec2 p) {
    p.x = abs(p.x);

    if (p.y + p.x > 1.0)
        return sqrt(dot(p - vec2(0.25, 0.75), p - vec2(0.25, 0.75))) - sqrt(2.0) / 4.0;

    return sqrt(min(dot(p - vec2(0.00, 1.00), p - vec2(0.00, 1.00)),
        dot(p - 0.5 * max(p.x + p.y, 0.0), p - 0.5 * max(p.x + p.y, 0.0)))) * sign(p.x - p.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord * 2.5 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for (float i = 0.0; i < 6.5; i++) {
        uv = fract(uv * 1.5) - 0.5;

        float d = length(uv) * exp(-length(uv0));

        vec3 col = palette(length(uv0) + i*.4 + iTime * 0.4);

        d = sin(d * 8.0 + iTime) / 8.0;
        d = abs(d);
        d = 0.02 / d;

        float heartDist = sdHeart(uv);
        float fill = smoothstep(0.03, 0.02, heartDist);

        finalColor += col * d * fill;
    }

    fragColor = vec4(finalColor, 1.0);
}
