vec3 palette( float t ) {
    vec3 a = vec3(0.6*abs(sin(t)), 0.1, 0.1);
    vec3 b = vec3(0.4, 0.4, 0.5*abs(tan(t)));
    vec3 c = vec3(0.8, 1.*abs(cos(t)), 1.0);
    vec3 d = vec3(0.263,0.4,0.5);

    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec3 finalColor = vec3(0.0);


    //Cercle 
    float d = length(uv);
    d = sin(8.*d+iTime)/12.;
    d = abs(d);
    d = pow(0.01 / d, 1.2);

    //
    uv = fract(uv *d) - 0.5;

    //square 
    float c = abs(uv.x)+abs(uv.y);
    c = sin(9.*c+2.5*iTime)/(0.1*(9.*c+2.5*iTime));
    c=abs(c);
    c=pow(0.01/c,1.2);


    finalColor += palette((0.3+c)*(0.3+d));

    fragColor = vec4(finalColor, 1.0);
}
