float Variation = 0.0;
float colorWidth = 0.025;
vec4 colorArrow = vec4(0,255.0/255.0,238.0/255.0,1.0);
int arrowDeformation = 2;

float sdfArrow(vec2 uv)
{

    float vari = Variation-5.0 > 20.0 ? 20.0 : Variation-5.0;

    float slope1 = - vari * 0.1;
    float slope2 = - vari * 0.04;
    float intercept1 =  vari * 0.045;
    float intercept2 =  vari * 0.02;

    float y1 = slope1 * abs(uv.x - 0.5) + intercept1;
    float y2 = slope2 * abs(uv.x - 0.5) + intercept2;

    float d1 = abs(y1- uv.y); 
    float d2 = abs(y2- uv.y);

    return  y1>=y2? min(d1 - colorWidth,  d2 - colorWidth):1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float as = 4.0 / iResolution.y; //For anti-aliasing
    Variation = 30.0*abs(sin(iTime));
        // colorize
    if(Variation >= 5.0)
    {
        float smoothD = smoothstep(as, 0.00, sdfArrow(uv));
        fragColor = mix(vec4(0.0),colorArrow, smoothD);    
    }
    else
        fragColor = colorArrow;

}
