const float TPI = 6.28318530718; 
const int MAX_ITER = 100;
const float BOUND = 100.0;

void mainImage(out vec4 fragColor, in vec2 fragCoord)

{
    vec2 pan = vec2(0.32, 1.0); //adjust possition :) 
    float duration = 15.0; 
    float pauseDuration= 0.0;
    float minZoom= 0.00002;
    float maxZoom=7.5;
    float t = mod(iTime, duration+1.1);
    float normalizedT= t/(duration-pauseDuration);
    float zoomLevel = minZoom*pow(maxZoom/minZoom,normalizedT);

    zoomLevel=mix(minZoom,zoomLevel,smoothstep(0.0,pauseDuration, t));


    vec2 uv = ((fragCoord - 0.5*iResolution.xy)/min(iResolution.y,iResolution.x)
    *zoomLevel+pan)*2.0-vec2(2.0);
    ///c = 0.984808 + 0.173648i
    ///c = 1.17462 + 0.427525i
    vec2 z = uv;
    vec2 c = vec2(0.301, 1.0); 

    int i;
    for (i = 0; i < MAX_ITER; i++) {

        vec2 sin_z = vec2(sin(z.x) * cosh(z.y), cos(z.x) * sinh(z.y));
        z = vec2(c.x*sin_z.x - c.y*sin_z.y, c.x*sin_z.y + c.y*sin_z.x);

        if (dot(z, z) > BOUND * BOUND) {
            break;        

        }
    }


    float iter_ratio = float(i) / float(MAX_ITER);
    vec3 col = 0.5 + 0.5 * cos(iTime + vec3(0.6, 0.8, 1.0) + 2.0 * TPI * vec3(iter_ratio));

    fragColor = vec4(col, 1.0);
}
