vec3 palette( float t)
{
    vec3 a = vec3(0.840,0.748,0.840);
    vec3 b = vec3(-0.083,0.297,0.537);
    vec3 c = vec3(1.132,2.587,1.786);
    vec3 d = vec3(-0.083,0.407,1.237);

    return a + b*cos( 6.28318*(c*t+d) );
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 uv2 = uv;

    uv -= .5;

    uv = fract(uv*15.);

    uv -= .5;

    uv.x *= iResolution.x/iResolution.y;

    uv *= 2.;

    vec3 fcol = vec3(1.,1.,1.);



    vec2 p = uv;

    vec2 b = vec2(.2,.2);


    //+ min(max(d.x,d.y),0.0)

    vec2 d = abs(p)-b;

    //d = sign(d+iTime);

    float t = length(max(d,.0));//+ min(max(d.x,d.y),0.0) ;




    t = sin(t*10.+iTime*2.)/20.;

    t -= 0.01;

    t = abs(t);

    t = smoothstep(.0,.4,t);

    t = 0.02/t;





    //t -= .5;

    //t = abs(t);

    //t *= sin(iTime);

    //t = pow(t,1.2);

    //t = sin(t*4.)/4.;


    vec3 col = vec3(1.,1.,1.);



    col = palette(length(uv)+.4 + iTime/100.);

    col *= t;

    col *= palette(length(uv2));

    fcol *= col;


    //float r = smoothstep(.5,.7,t);


    fragColor = vec4(fcol,1.0);
}
