#define pi 3.142

void mainImage( out vec4 O, vec2 uv )
{   vec2 R = iResolution.xy;
    uv = ( uv +uv - R ) / R.y;
    float t=iTime/2., d=1.;

    for (float i=0.; i<2.*pi; i+=pi/(50.))
    d = min(d,
            length(uv-vec2(sin(5.*i-t),
            cos(7.*i-t))-.75*sin(i)));

    O = vec4(1.-d*3.)*vec4(.8,.95,.2,1);
}
