#define size 14
//binary brightness 0001
int[size] i1=int[size](
0x000F,
0x06ee,
0x0ef0,
0x3ff0,
0x7ff8,
0x7fcc,
0x7f04,
0x7fc4,
0x7f00,
0x3fc0,
0x7f80,
0xff00,
0xfe1c,
0xf83c);

//binary brightness 0010
int[size] i2=int[size](
0x0000,
0x0000,
0x003e,
0x0e7e,
0x0626,
0x1732,
0x1ff8,
0x0d18,
0x19fc,
0x1d3c,
0x4c7c,
0xac1c,
0xe000,
0xe000);

//binary brightness 0100
//int[size] i3=int[size](0x0);

//binary brightness 1000
//int[size] i4=int[size](0x0);
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    ivec2 z = ivec2(fragCoord)/8;

int B1 = ((i1[z.x]>>z.y&1)*((z.x>(size-1))?0:1)*((z.y>31)?0:1));
int B2 = ((i2[z.x]>>z.y&1)*((z.x>(size-1))?0:1)*((z.y>31)?0:1));

vec3 a;

//4 bit

if (float( B1  &  B2) == 0.)a=vec3(1);
if (float( B1  & ~B2) == 1.)a=vec3(.8,.2,.2);
if (float(~B1  &  B2) == 1.)a=vec3(.2,.2,.8);
if (float( B1  &  B2) == 1.)a=vec3(.8,.7,.1);


fragColor = vec4(a,0.);
}
