float circleSDF(vec2 p, vec2 center, float radius) {
    return length(p - center) - radius;
}

float smoothUnion(float a, float b, float k) {
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}

float frogSDF(vec2 p) {
    float body = circleSDF(p, vec2(0.0, -0.2), 0.3);
    float head = circleSDF(p, vec2(0.0, 0.15), 0.15);
    float leftEye = circleSDF(p, vec2(-0.1, 0.25), 0.05);
    float rightEye = circleSDF(p, vec2(0.1, 0.25), 0.05);

    float leftLegUpper = circleSDF(p, vec2(-0.3, -0.3), 0.1);
    float leftLegLower = circleSDF(p, vec2(-0.45, -0.45), 0.07);
    float rightLegUpper = circleSDF(p, vec2(0.3, -0.3), 0.1);
    float rightLegLower = circleSDF(p, vec2(0.45, -0.45), 0.07);

    float k = 0.05;
    float frogShape = smoothUnion(body, head, k);
    frogShape = smoothUnion(frogShape, leftEye, k);
    frogShape = smoothUnion(frogShape, rightEye, k);
    frogShape = smoothUnion(frogShape, leftLegUpper, k);
    frogShape = smoothUnion(frogShape, leftLegLower, k);
    frogShape = smoothUnion(frogShape, rightLegUpper, k);
    frogShape = smoothUnion(frogShape, rightLegLower, k);

    return frogShape;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float sdf = frogSDF(uv);
    float edgeWidth = 0.005;
    float edge = smoothstep(-edgeWidth, edgeWidth, sdf);

    vec3 bgColor = mix(vec3(0.1, 0.7, 0.4), vec3(0.1, 0.4, 0.2), (uv.y + 1.0) / 2.0);
    vec3 frogColor = vec3(0.0, 0.6, 0.0);
    vec3 color = mix(frogColor, bgColor, edge);

    fragColor = vec4(color, 1.0);
}
