vec3 colorRamp(float t) {
    return mix(
        vec3(0.9, 0.3, 0.6),
        vec3(0.2, 0.8, 0.5),
        0.5 + 0.5 * cos(t)
    );
}

float plasma(vec2 uv, float t) {
    float f1 = sin(uv.x * 6.0 + t);
    float f2 = sin(uv.y * 6.0 - t);
    float f3 = sin((uv.x + uv.y) * 6.0 + t);
    float f4 = sin(sqrt(uv.x * uv.x + uv.y * uv.y) * 6.0 - t);

    return (f1 + f2 + f3 + f4) * 0.25;
}

vec3 glow(vec3 col, float intensity, float radius) {
    float glowFactor = smoothstep(radius, radius - 0.1, intensity);
    return col * (1.0 + glowFactor);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float time = iTime * 0.5;

    float p = plasma(uv, time);

    vec3 col = colorRamp(p * 3.0 + time);

    col = glow(col, p, 0.1); // Apply glow effect

    fragColor = vec4(col, 1.0);
}
