// Function to convert HSV to RGB
vec3 hsv2rgb(vec3 c) {
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalize coordinates to [0, 1]
    vec2 p = fragCoord / iResolution.xy;

    // Calculate the distance from the center
    float dist = length(p - 0.5);

    // Calculate angle for the eye movement
    float eyeAngle = atan(p.y - 0.5, p.x - 0.5) + iTime;

    // Eye iris and pupil
    vec3 eyeIrisColor = vec3(0.0, 0.5, 1.0);
    vec3 eyePupilColor = vec3(0.0);
    float eyeRadius = 0.1;
    float pupilRadius = 0.03;

    // Calculate the position of the eye
    vec2 eyePosition = vec2(0.2 * cos(eyeAngle) + 0.5, 0.2 * sin(eyeAngle) + 0.5);

    // Calculate the color-changing ghosts in the background
    float soundValue = texture(iChannel0, vec2(0.5, 0.5)).x; // Sample sound input (adjust coordinates as needed)

    // Ghost color
    vec3 ghostColor = hsv2rgb(vec3(fract(soundValue + iTime), 1.0, 1.0));

    // Ghost size
    float ghostSize = 0.1 + 0.1 * soundValue;

    // Ghost position
    vec2 ghostPosition = vec2(0.5 * cos(iTime) + 0.5, 0.5 * sin(iTime) + 0.5);

    // Create the background ghosts
    float ghostEffect = smoothstep(0.0, ghostSize, dist - length(fragCoord - ghostPosition));
    vec3 background = mix(vec3(0.0), ghostColor, ghostEffect);

    // Create the eye
    float eyeEffect = smoothstep(0.0, eyeRadius, dist - length(fragCoord - eyePosition));
    float pupilEffect = smoothstep(0.0, pupilRadius, dist - length(fragCoord - eyePosition));
    vec3 eye = mix(eyePupilColor, eyeIrisColor, eyeEffect);
    eye = mix(eye, eyePupilColor, pupilEffect);

    // Combine the background, eye, and ghosts
    vec3 finalColor = background + eye;

    fragColor = vec4(finalColor, 1.0);
}
