float factor = 2.0;
float segments = 16.0;
float zoom = 4.0;
float smoothfactor = .01;
float tau = 3.14159265359*2.0;

vec4 test(vec2 uv) {
    return vec4(uv.x, 0.0, uv.y, 1.0);
}

vec4 basic(vec2 uv) {
    return vec4(vec3(pow(smoothstep(0.0, 1.0, sin(-uv.y*tau)*(smoothfactor*iResolution.y)+0.5), 1.0/2.2)), 1.0);
}

vec4 trees(vec2 uv, float foo, float bar) {
    float linedist = abs(abs(uv.y-0.5)-(uv.x*0.25*sqrt(2.0)/pow(sqrt(2.0), uv.x)));
    float thiccness = 0.1*pow(factor, -uv.x);
    return vec4(vec3(pow(smoothstep(1.0, 0.0, linedist/thiccness), 1.0/2.2)), 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv0 = fragCoord/iResolution.xy;
    vec2 uv = uv0;
    //uv = vec2(1.0-uv.y, uv.x);

    float t = iTime / pow(factor, segments-5.0);

    uv.x = uv.x * segments; // repeat n times
    uv.y -= 0.5; // vertical centering
    uv.y *= pow(factor, mod(-uv.x, 1.0)) * zoom; // exponential scaling
    float speed = pow(factor, floor(uv.x));
    uv.y += t * speed; // make each wheel faster than the previous

    // Output to screen
    //fragColor = test(mod(uv, 1.0));
    fragColor = uv.y < 0.0 || uv.y > speed ? vec4(vec3(0.0), 1.0) : trees(mod(uv, 1.0), uv.y, speed);
    //fragColor = abs(uv0.y-0.5) <0.004 ? vec4(1.0, 0.0, 0.0, 1.0) : fragColor; // red line
}
