float uvToSin(vec2 t_uv,float offset){
    float d = length(t_uv*offset);
    float sind = sin(d*1.6-iTime*2.)*0.5+0.5;
    sind=0.2/sind;
    return sind;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy *2. -1.;
    uv.x*=iResolution.x/iResolution.y;

    float d = 1. - length(uv);

    vec2 uvr = uv + vec2(.25,0.);
    vec2 uvl = uv - vec2(.25,0.);
    vec2 uvu = uv + vec2(0.,.25);
    vec2 uvd = uv - vec2(0.,0.25);

    uv*=9.;uvr*=9.;uvl*=9.;uvu*=9.;uvd*=9.;

    float sinf=0.;float sinfp=0.;float sinfpp=0.;

    for(float i = 0.;i<1.;i+=1.){
        uv*=2.;uvr*=2.;uvl*=2.;uvu*=2.;uvd*=2.;
        sinf += (uvToSin(uv,1.)*uvToSin(uvr,1.)*uvToSin(uvl,1.)*uvToSin(uvu,1.)*uvToSin(uvd,1.));
        sinfp += (uvToSin(uv,1.025)*uvToSin(uvr,1.025)*uvToSin(uvl,1.025)*uvToSin(uvu,1.025)*uvToSin(uvd,1.025));
        sinfpp += (uvToSin(uv,1.05)*uvToSin(uvl,1.05)*uvToSin(uvu,1.05)*uvToSin(uvd,1.05)); 
    }
    sinf=0.03/sinf;sinfp=0.03/sinfp;sinfpp=0.03/sinfpp;
    fragColor = vec4(sinf,sinfp,sinfpp,1.0);
}
