void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  float speed = 1.0;
  float a = 100000.;
  // 0 and 1 switch between the two modes.
  float phase = smoothstep(0.4, 0.6, sin(0.4 * iTime));
  float zoom_inv = 450. * (1. + 4. * (1. - phase));
  float magic = phase + 0.001;
  vec2 p = zoom_inv * 2. * (fragCoord / iResolution.xy - 0.5);
  float x = p.x;
  float y = p.y;

  float color0 = 0.;
  float color1 = 0.;
  // Loosely based on Julia set.
  for (int i = 0; i < 5; i++) {
    color1 = x + y;
    float x0 = x * x - y * y + a * cos(speed * iTime);
    y = x * y + x * y + a * sin(speed * iTime);
    color0 = x;
    x = x0;
    x *= magic;
    y *= magic;
  }
  fragColor = vec4(color1, color0, color0, 1);
}
