float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float sdEquilateralTriangle( in vec2 p, in float r )
{
    const float k = sqrt(3.0);
    p.x = abs(p.x) - r;
    p.y = p.y + r/k;
    if( p.x+k*p.y>0.0 ) p = vec2(p.x-k*p.y,-k*p.x-p.y)/2.0;
    p.x -= clamp( p.x, -2.0*r, 0.0 );
    return -length(p)*sign(p.y);
}

float sdTriangleIsosceles( in vec2 p, in vec2 q )
{
    p.x = abs(p.x);
    vec2 a = p - q*clamp( dot(p,q)/dot(q,q), 0.0, 1.0 );
    vec2 b = p - q*vec2( clamp( p.x/q.x, 0.0, 1.0 ), 1.0 );
    float s = -sign( q.y );
    vec2 d = min( vec2( dot(a,a), s*(p.x*q.y-p.y*q.x) ),
                  vec2( dot(b,b), s*(p.y-q.y)  ));
    return -sqrt(d.x)*sign(d.y);
}

// Crazy Pumpkin
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1 centered at origin)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 r =  2.0*vec2(fragCoord.xy - 0.5*iResolution.xy)/iResolution.y;

    // Background
    vec3 col = vec3(0.);

    // ground
    col = mix(col, 0.5*vec3(150./255., 75./255., 0.), 1.-smoothstep(0.19, 0.25, uv.y));

    // Body
    col = mix(col, vec3(0.9, 0.5, 0.), smoothstep(0.,0.03,-sdCircle(r, 0.9)));
    vec3 cInside = vec3(1., 1., 0.);

    //Carve eyes
    vec2 rMirrorX = vec2(abs(r.x), r.y);
    float eyes = smoothstep(0., 0.03, -sdEquilateralTriangle(rMirrorX-vec2(0.4, 0.4), 0.2));
    col = mix(col, cInside, eyes);

    // Carve nose
    float nose = smoothstep(0., 0.03, -sdEquilateralTriangle(rMirrorX-vec2(0., 0.), 0.1));
    col = mix(col, vec3(0.), nose);


    fragColor= vec4(col, 1.0);

}
