#define S(v) smoothstep(ss,-ss,v)

float triangleDist(vec2 p, float radius) {
	return max(	abs(p).x * 0.866025 + p.y * 0.5, -p.y) -radius * 0.5;
}

vec2 flipV(vec2 p) {
    return vec2(p.x, p.y * -1.);
}

vec2 translate(vec2 p, float x, float y) {
	return vec2(p.x + x, p.y + y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5*iResolution.xy) / iResolution.y;
    vec2 p = uv;
    float ss = .75/iResolution.y;
    float size = .4;

    vec3 bg = vec3(0.949,0.949,0.714);
    vec3 c1 = vec3(1.,0.427,0.);
    vec3 c2 = vec3(0.992,0.275,0.008);

    vec3 col = bg;
    col = mix(col, c1, S(triangleDist(translate(p,size * -.8,size/4.),size)));
    col = mix(col, c2, S(triangleDist(translate(p,size * -.55,size/4.),size)));
    col = mix(col, c2, S(triangleDist(flipV(translate(p,size * .55,-size/4.)),size)));
    col = mix(col, c1, S(triangleDist(flipV(translate(p,size * .8,-size/4.)),size)));

    fragColor = vec4(col,1.0);
}
