void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float time = iTime * 0.2;

    float t = pow(time, 0.5);
    float p = pow(10.0, fract(sin(dot(uv, vec2(12.9898, 78.233))) * 43758.5453));

    vec3 color = vec3(
        0.3 + 0.3 * cos(2.0 + t + p),
        0.3 + 0.3 * cos(1.0 + t + p),
        0.3 + 0.3 * cos(0.5 + t + p)
    );

    vec2 coord = uv * 0.5;
    coord *= 2.0;
    float r = length(coord);
    float angle = atan(coord.y, coord.x);
    float orbit = sin(r * 2.0 - time * 3.0) * 0.3 + 0.3;
    float dist = pow(1.0 - r, 2.0);
    dist = pow(dist, 10.0) * orbit;

    vec2 shape = vec2(cos(angle) * dist, sin(angle) * dist);
    vec3 shapeColor = vec3(
        0.5 + 0.5 * sin(shape.x + time * 2.0),
        0.5 + 0.5 * sin(shape.y + time * 2.0),
        0.5 + 0.5 * cos(shape.x * shape.y + time * 2.0)
    );

    vec3 finalColor = mix(color, shapeColor, .8);

    finalColor = mix(finalColor, vec3(1.0, 1.0, 1.0), pow(r, 8.0));

    fragColor = vec4(finalColor, 1.0);
}
