void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = mix(vec3(0), vec3(0, 0.2, 0.0), uv.x);
    if (uv.y < 0.5) col += vec3(texture(iChannel0, fragCoord/iChannelResolution[0].xy).rgb - 0.5) * (1.0/255.0);
    fragColor = vec4(col,1.0);
}
