vec3 pastelColor(float angle) {
    return vec3(
        0.9 + 0.3 * sin(angle + 0.0),
        0.9 + 0.3 * sin(angle + 2.0944),
        0.9 + 0.3 * sin(angle + 4.1888)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
float nice1 = 0.02 * 1. * sin(iTime / 2.);
float nice2Speed = 1. / 10. * 2. * sin(iTime / 3.);
    vec2 uv = fragCoord.xy / iResolution.xy;
    float time = iTime * 0.5;

    int numLines = 100;
    if (iTime > 10.) {numLines = int(iTime * 100.);}

    vec3 backgroundColor = pastelColor(time);
    vec3 color = backgroundColor;

    for (int i = 0; i < int(min(float(iTime * 500.),float(2503))); ++i) {
        float x = float(i) / float(numLines - 1) - 0.3;
        float lineWidth = 1.0 / iResolution.x;

        float yPos = uv.y + nice2Speed * (sin(time * 2.0 + float(i) * 0.1) + 1.0);
        float displacement = nice1 * sin(yPos * 30.0) * 0.5 + 0.5;
        x += displacement;

        float lineMask = 1.0 - smoothstep(0.0, lineWidth, abs(uv.x - x));
        color = mix(color, vec3(0.0), lineMask);
    }

    fragColor = vec4(color, 1.0);
}
