void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalize so Y coordinate between [-1, +1]
    vec2 pos = fragCoord/iResolution.y*2.f - 1.f;

    // colors to interpolate
    vec3 col0 = vec3(1,.5,0), col1=vec3(0,.5,1);

    // interpolate color
    float radius = length(pos);
    vec3 col = mix(col0,col1,radius);

    if(radius <= 1.f) fragColor = vec4(col,1.0);
}
