void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    // Time varying pixel color
    vec3 col = vec3(0.,0.,0.2);

    if (fragCoord.y < iResolution.y/3.+10.*sin(fragCoord.x/20.+iTime*0.5)){col += 0.2;}
    if (fragCoord.y < iResolution.y/3.7+10.*sin(fragCoord.x/20.+iTime*1.0)){col += 0.2;}
    if (fragCoord.y < iResolution.y/5.6+10.*sin(fragCoord.x/20.+iTime*1.5)){col += 0.2;}
    float d = length(uv);
    float m = 0.1/d;
    col += m-0.5;

    // Output to screen
    fragColor = vec4(col,1.0);
}
