vec2 CDIV(vec2 a,vec2 b){
    float r = length(a);
    float theta = atan(a.y,a.x);

    float r2 = length(b);
    float theta2 = atan(b.y,b.x);

    r/=r2;
    theta-=theta2;

    return r * vec2(cos(theta),sin(theta));
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
    {


    vec2 uv = ((fragCoord - .5 * iResolution.xy)/iResolution.y) * 3.;
    vec3 col = vec3(0);
    float r = 20.;
    for(float x = 0.;x < r;x++){
        for(float y = 0.; y < r;y++){
            vec2 z = ((vec2(x,y))/(r-1.)) -.5;
            z *=10.;
            for(int i = 0; i< 100;i++){
               z = vec2(1,0) + CDIV(vec2(1,0),z);
               if(length(z - uv)<.01)col += 1.;
            }

       }


    }


    // Output to screen
    fragColor = vec4(col,1.0);
}
