#define s(x) smoothstep(0., .7, x)
#define iters 7

//#define darkmode
#ifdef darkmode 
    #define dark 1.-
#else
    #define dark
#endif

//hash by IQ https://www.shadertoy.com/view/XlXcW4
const uint k = 1103515245U;
vec3 hash( uvec3 x )
{
    x += k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)*(1.0/float(0xffffffffU));
}

vec2 m(vec2 uv, float s){

    vec2 a,x,p=uv;

    for (int i = 0; i++<2<<iters;){

        vec2 t = p, b = t*0.;
        a = uv;
        a = mix(a, t               , s(s-b++)); // Julia set becomes this at a certain input ðŸ¤”
        a = mix(a, vec2(uv.x,  t.y), s(s-b++)); 
        a = mix(a, vec2(t.  x,uv.y), s(s-b++));
        a = mix(a, vec2(t.  y,uv.x), s(s-b++)); // Not surprised its asymmetrical.
        a = mix(a, vec2(uv.y,  t.x), s(s-b++)); // Curly area below looks cool. Top edge might be interesting
        a = mix(a, uv.     yx      , s(s-b++)); // Favourite one
        a = mix(a, t.      yx      , s(s-b++)); // What's this?! Seems rotationally symmetrical
                                                // which is incredibly weird. Why 3? Huh???
        a = mix(a, uv.     xy      , s(s-b++)); // Restore to loop

        p.x = t.x*t.x - t.y*t.y + a.x;
        p.y = 2.*t.x*t.y + a.y;

        if (length(p) > 2.){
            x = vec2(dark exp(-float(i)/4e1), i);
            break;
        }
    }

    return x;

}

void mainImage( out vec4 O, vec2 U ){
    vec2 r = iResolution.xy;
    vec2 uv = U/r;
    vec2 cuv = (2.*U-r)/r.y;

    float s = mod(iTime/2., 8.);
    cuv *= 2.;

    int ssaa = 1 + int(iFrameRate/10.);

    vec2 x = uv*0.;
    for (int i = 0; i++<ssaa;){

        x += m(cuv + (hash(uvec3(i)).xy*2.-1.)/r.y, s);

    }
    x /= float(ssaa);

    O = x.xxxx;
}
