const float P = 3.1415926;

vec3 h2r(in vec3 c) {
    vec3 rgb = clamp(abs(mod(c.x * 6.0 + vec3(0.0, 4.0, 2.0), 6.0) - 3.0) - 1.0, 0.0, 1.0);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

uvec3 k = uvec3(0x456789abu, 0x6789ab45u, 0x89ab4567u);
uvec3 u = uvec3(1, 2, 3);

uvec3 uh(uvec3 n){
    n ^= (n.yzx << u);
    n ^= (n.yzx >> u);
    n *= k;
    n ^= (n.yzx << u);
    return n * k;
}

float g(vec3 l, vec3 p){ 
    uvec3 n = floatBitsToUint(l); 
    uint ind = uh(n).x >> 28; 
    float u = ind < 8u ? p.x : p.y;
    float v = ind < 4u ? p.y : ind == 12u || ind == 14u ? p.x : p.z;
    return ((ind & 1u) == 0u? u: -u) + ((ind & 2u) == 0u? v : -v);
}

float p3(vec3 p){
    vec3 n = floor(p);
    vec3 f = fract(p);
    float[8] v;
    for (int k = 0; k < 2; k++ ){
        for (int j = 0; j < 2; j++ ){
            for (int i = 0; i < 2; i++){
                v[i+2*j+4*k] = g(n + vec3(i, j, k), f - vec3(i, j, k)) * 0.70710678;
            }

        }
    }
    f = f * f * f * (10.0 - 15.0 * f + 6.0 * f * f);
    float[2] w;
    for (int i = 0; i < 2; i++){
        w[i] = mix(mix(v[4*i], v[4*i+1], f[0]), mix(v[4*i+2], v[4*i+3], f[0]), f[1]);
    }
    return 0.5 * mix(w[0], w[1], f[2]) + 0.5;
}

void mainImage(out vec4 f, in vec2 c) {
    vec2 p = c.xy/iResolution.xy;
    p = 8.0 * p.xy + sin(p3(vec3(p.y)) * 1.0) * 1.0;
    float v =  p3(vec3(p.x * 2.0, p.y * 2.0, iTime * 0.8));
    f.rgb = h2r(vec3(v * 10.0, 1.0, 1.0));
    f.a = 1.0;
}
